import React from 'react';
import { connect } from 'react-redux';
import {editStream, fetchStream} from '../../actions';
import StreamForm from './StreamForm';
import history from '../../history';


class StreamEdit extends React.Component {
    componentDidMount () {
        this.props.fetchStream(this.props.streamId);
    }

    onSubmit = (formValues, userId) => {
        // console.log(formValues);
        this.props.editStream(this.props.streamId, {...formValues, userId: userId});
    }

    render () {
        return (
            <div>
                <h3>Edit a Stream</h3>
                <StreamForm 
                    initialValues={{title: this.props.stream.title, description: this.props.stream.description }}
                    onSubmit={this.onSubmit} 
                />
            </div>
        );
    }
}

const mapStateToProps = state => {
    const id = history.location.pathname.split('/')[3];
    return {
        userId: state.auth.userId,
        stream: state.streams[id],
        streamId: id
    };
}

export default connect(mapStateToProps, {editStream, fetchStream})(StreamEdit);
