import React from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import {fetchStreams, deleteStream} from '../../actions';

class StreamList extends React.Component {
    componentDidMount () {
        this.props.fetchStreams();
    }

    renderButton = (stream) => {
        if(stream.userId === this.props.currentUserId){
            return (
                <div className="right floated content">
                    <Link to={`/streams/edit/${stream.id}`} className="ui button">
                        Edit
                    </Link>
                    {/* <button className="ui button negative" onClick={() => this.props.deleteStream(stream.id)}>
                        Delete
                    </button> */}
                    <Link to={`/streams/delete/${stream.id}`} className="ui button negative">
                        Delete
                    </Link>
                </div>
            )
        }
    }

    renderCreate = () => {
        if(this.props.isSignedIn){
            return (
                <div style={{textAlign: 'right'}}>
                    <Link to="/streams/new" className="ui button primary">
                        Create Stream
                    </Link>
                </div>           
            );
        }
    }

    renderList = () => {
        return this.props.streams.map(stream => {
            return (
            <div className="item" key={stream.id}>
                {this.renderButton(stream)}
                <i className="large middle aligned icon camera" />
                <div className="content">
                    <Link to={`/streams/${stream.id}`} className="header" >
                        {stream.title}
                    </Link>
                    <div className="description">
                        {stream.description}
                    </div>
                </div>
            </div>
            );
        });
    }

    render (){
        return (
            <div>
                <h2>Streams</h2>
                <div className="ui celled list">{this.renderList()}</div>
                <div>
                    {this.renderCreate()}
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        streams: Object.values(state.streams),
        isSignedIn: state.auth.isSignedIn,
        currentUserId: state.auth.userId
    };
}

export default connect(mapStateToProps, {fetchStreams, deleteStream})(StreamList);
