import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import {createStream} from '../../actions';

class StreamCreate extends React.Component {
    errorHandle ({ error, touched}) {
        if(touched && error){
            return (
                <div className="ui error message" >
                    <div className="header">{error}</div>
                </div>
            );
        }
    }

    renderInput = (props) => {
        // console.log(props.meta);
        const className = `field ${props.meta.error && props.meta.touched ? 'error' : ''}`;
        return (
            <div className={className}>
                <label>{props.label}</label>
                <input {...props.input} autoComplete="off" />
                {this.errorHandle(props.meta)}
            </div>
        );
    }

    onSubmit = (formValues) => {
        // console.log(formValues);
        this.props.createStream({...formValues, userId: this.props.userId});
    }

    render () {
        // console.log(this.props);
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error" >
                <Field name="title" component={this.renderInput} label="Enter Title:"/>
                <Field name="description" component={this.renderInput} label="Enter Description:" />
                <button className="ui button primary" >Submit</button>
            </form>
        );
    } 
};

const validate = (formValues) => {
    const errors = {};
    if(!formValues.title){
        errors.title = "You must enter a title!"
    }
    if(!formValues.description){
        errors.description = "You must enter a description!"
    }
    return errors;
};

const formWrapped = reduxForm({
    form: 'streamCreate',
    validate
})(StreamCreate);

const mapStateToProps = state => {
    return {userId: state.auth.userId};
}

export default connect(mapStateToProps, {createStream})(formWrapped);
