import React from 'react';
import { connect } from 'react-redux';
import {editStream, fetchStream} from '../../actions';
import StreamForm from './StreamForm';
import history from '../../history';


class StreamEdit extends React.Component {
    componentDidMount () {
        const streamId = history.location.pathname.split('/')[3];
        this.props.fetchStream(streamId);
    }

    onSubmit = (formValues, userId) => {
        // console.log(formValues);
        const streamId = history.location.pathname.split('/')[3];
        this.props.editStream(streamId, {...formValues, userId: userId});
    }

    render () {
        return (
            <div>
                <h3>Edit a Stream</h3>
                <StreamForm 
                    initialValues={{title: '123', description: '456'}}
                    onSubmit={this.onSubmit} 
                />
            </div>
        );
    }
}

export default connect(null, {editStream, fetchStream})(StreamEdit);
