import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import {editStream} from '../../actions';
import history from '../../history';


class StreamEdit extends React.Component {
    errorHandle ({ error, touched}) {
        if(touched && error){
            return (
                <div className="ui error message" >
                    <div className="header">{error}</div>
                </div>
            );
        }
    }

    renderInput = (props) => {
        var content = "";
        if(this.props.stream){
            content = `${props.input.name === "title" ? this.props.stream.title : this.props.stream.description}`;
        }
        this.setState({content: content});
        console.log(props);
        const className = `field ${props.meta.error && props.meta.touched ? 'error' : ''}`;
        return (
            <div className={className}>
                <label>{props.label}</label>
                <input 
                    {...props.input} 
                    autoComplete="off" 
                />
                {this.errorHandle(props.meta)}
            </div>
        );
    }

    onSubmit = (formValues) => {
        // console.log(formValues);
        this.props.editStream(this.props.streamId, {...formValues, userId: this.props.userId});
    }

    render () {
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error" >
                <Field name="title" component={this.renderInput} label="Enter New Title:"/>
                <Field name="description" component={this.renderInput} label="Enter New Description:" />
                <button className="ui button primary" >Submit</button>
            </form>
        );
    }
}

const validate = (formValues) => {
    const errors = {};
    if(!formValues.title){
        errors.title = "You must enter a title!"
    }
    if(!formValues.description){
        errors.description = "You must enter a description!"
    }
    return errors;
};

const formWrapped = reduxForm({
    form: 'streamEdit',
    validate
})(StreamEdit);

const mapStateToProps = state => {
    const id = history.location.pathname.split('/')[3];
    return {
        userId: state.auth.userId,
        stream: state.streams[id],
        streamId: id
    };
}

export default connect(mapStateToProps, {editStream})(formWrapped);
