import jasonPlaceholder from '../apis/jasonPlaceholder';

export const fetchPosts = () => {
    return async (dispatch, getState) =>{
        const response = await jasonPlaceholder.get('/posts');

        dispatch({
                type: "FETCH_POSTS",
                payload: response
        });
    };
};
